  }

  return (
    <div className="App">
      <div className="addTask">
        <input onChange={handleChange}/>
        <button onClick={addTask}> Add Task </button>
      </div>
      <div className="list">
        {todoList.map((task) => {
          return (
            <div>
              <h1>{task}</h1>
              <button inClick={() => deleteTask(task)}> X </button>
              </div>
            );
        })}
      </div>
    </div>
  );
}

export default App;
